
import base64
import os

base64_string = ""

# add padding
missing_padding = len(base64_string) % 4
if missing_padding:
    base64_string += '=' * (4 - missing_padding)

# decode
image_data = base64.b64decode(base64_string)

# write to file
public_path = "/root/code/portfolio-website/public/static/images/preview-optimized.jpg"
os.makedirs(os.path.dirname(public_path), exist_ok=True)
with open(public_path, "wb") as f:
    f.write(image_data)
print(f"Image saved to {public_path}")
